// NOTE: Card is the parent component for Stopwatch
import React from 'react';
import './Card.css';
import Stopwatch from './Stopwatch';

const lockCombo = ["h", "t", "t", "p", "s"];
const success = ["Success! You've opened lock number 4!"];
const failure = ["Please try again!"];

class Card extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            positions: {
                posOne: "",
                posTwo: "",
                posThree: "",
                posFour: "",
                posFive: ""
            },
            inputDisabled: false
        }
    }

    // func to check users input against lockCombo
    checkAnswer = () => {
        // create a var that takes the state (which will be relected by the user's input) and returns the values as an arr
        const lockInputArr = Object.values(this.state.positions);

        // loop through the length of our arr
        for (let i = 0; i < lockInputArr.length; i++) {
            // if the index of our two arr are not equal, alert failure, reset state with this.reset(), and return false
            if (lockInputArr[i] !== lockCombo[i]) {
                // reset();
                alert(failure);
                return false;
            }
        }
        // otherwise just alert success and return true
        alert(success);
        return true;
    }

    disableInput = () => {
        this.setState({
            inputDisabled: true
        });

        // TODO Finish logic for disableInput function
        // if (this.state.inputDisabled === true) {

        // }
    }

    // func to reset state to empty strings
    // reset = () => {
    //     this.setState({
    //         positions: {
    //             posOne: ""
    //         }
    //     });
    //     this.setState({
    //         positions: {
    //             posTwo: ""
    //         }
    //     });
    //     this.setState({
    //         positions: {
    //             posThree: ""
    //         }
    //     });
    // }

    // func to handle user input and change state to user input value
    handleInputChange = (e) => {
        const target = e.target;
        const value = target.value;
        const position = target.id;

        if (position === "1") {
            this.setState({
                positions: {
                    ...this.state.positions,
                    posOne: value
                }
            });
        } else if (position === "2") {
            this.setState({
                positions: {
                    ...this.state.positions,
                    posTwo: value
                }
            });
        } else if (position === "3") {
            this.setState({
                positions: {
                    ...this.state.positions,
                    posThree: value
                }
            });
        } else if (position === "4") {
            this.setState({
                positions: {
                    ...this.state.positions,
                    posFour: value
                }
            });
        } else if (position === "5") {
            this.setState({
                positions: {
                    ...this.state.positions,
                    posFive: value
                }
            });
        }
    }

    render() {
        return (
            <div>
                {/* Stopwatch child component */}
                <Stopwatch onTimerFinish={this.disableInput} />
                <div className="whole-card">
                    {/* positions to be populated upon user input */}
                    <div>
                        <span className="bg-dark-red dib br3 pa3 ma2 grow bw4 shadow-5">{this.state.positions.posOne}</span>
                        <span className="bg-dark-red dib br3 pa3 ma2 grow bw4 shadow-5">{this.state.positions.posTwo}</span>
                        <span className="bg-dark-red dib br3 pa3 ma2 grow bw4 shadow-5">{this.state.positions.posThree}</span>
                        <span className="bg-dark-red dib br3 pa3 ma2 grow bw4 shadow-5">{this.state.positions.posFour}</span>
                        <span className="bg-dark-red dib br3 pa3 ma2 grow bw4 shadow-5">{this.state.positions.posFive}</span>
                    </div>
                    <br />
                    {/* form to capture user inputs */}
                    <form>
                        <input type="text" maxLength="1" className="bg-dark-red dib br3 pa3 ma2 grow bw2 shadow-5" id="1" onChange={this.handleInputChange}></input>
                        <input type="text" maxLength="1" className="bg-dark-red dib br3 pa3 ma2 grow bw2 shadow-5" id="2" onChange={this.handleInputChange}></input>
                        <input type="text" maxLength="1" className="bg-dark-red dib br3 pa3 ma2 grow bw2 shadow-5" id="3" onChange={this.handleInputChange}></input>
                        <input type="text" maxLength="1" className="bg-dark-red dib br3 pa3 ma2 grow bw2 shadow-5" id="4" onChange={this.handleInputChange}></input>
                        <input type="text" maxLength="1" className="bg-dark-red dib br3 pa3 ma2 grow bw2 shadow-5" id="5" onChange={this.handleInputChange}></input>
                    </form>
                    {/* Unlock button that runs checkAnswer func onClick*/}
                    <div>
                        <button className="bg-dark-red dib br3 pa3 ma2 bw1 shadow-5 spin-button" variant="secondary" type="submit" onClick={this.checkAnswer}>Unlock</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default Card;
